import React, { memo } from 'react';
import renderHTML from 'react-render-html';

const RawHTML = ({ children }) => {
  const html = children.replace("<p>&nbsp;</p>", "");
  return(
    renderHTML(html)
  );
}

export default memo(RawHTML);

