import React, { FC, memo } from 'react';
import { FlatList } from 'react-native';
import styled from 'styled-components/native';
import Fonts from '../../../theme/fonts';
import Colors from '../../../theme/colors';
import QuestionItem from './QuestionItem';
import RawHTML from '../../../components/RawHTML';
import AudioPlayer from 'components/AudioPlayer';
import useWindowDimensions from 'hooks/useWindowDimensions';

interface Props {
  data: any;
  part3Index: number;
  testNumber: number;
  onSelectAnswer: (detailItem: any, questionPosition: number, selected: string, part3Index: number) => void;
  onCollapse: (part3Index: number) => void;
  onCollapseTranslation: (part3Index: number) => void;
}

interface StyledProps {
  size?: number;
  height?: number;
  width?: number;
  marginHorizontal?: number;
  paddingHorizontal?: number;
  paddingRight?: number;
  isCorrect?: boolean;
  isSelected?: boolean;
  isShown?: boolean;
}

interface ItemType {
  id: number;
  a: string;
  b: string;
  c: string;
  d: string;
  correct: string;
  question: string;
  selected?: string;
  submitted?: boolean;
}

const ItemContainer = styled.View<StyledProps>`
  width: 100%;
  background-color: ${Colors.SECOND_COLOR};
`;

const Text = styled(Fonts.Bold)`
  font-size: 18px;
  padding: 8px;
`;

const TestFlatList = styled(FlatList as new () => FlatList<ItemType>)`
  width: 100%;
  height: 100%;
`;

const ListContainer = styled.View`
  height: 100%;
  width: 100%;  
  padding-vertical: 8px;
`;

const Space = styled.View<StyledProps>`
  background-color: ${Colors.MAIN_COLOR};
  width: 100%;
  height: 1px;
`;

const Divider = styled.View<StyledProps>`
  background-color: ${Colors.MAIN_COLOR};
  width: 100%;
  height: 2px;
  margin-top: 8px;
  margin-bottom: 8px;
`;

const HTMLContainer = styled.View`
  padding-horizontal: 12px;
  padding-bottom: 8px;
`;

const Image = styled.Image<StyledProps>`
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  resize-mode: contain;
  align-self: center;
`;

const Part3Item: FC<Props> = ({
  data,
  part3Index,
  testNumber,
  onSelectAnswer,
  onCollapse,
  onCollapseTranslation,
}) => {
  const audioPath = `https://github.com/QuizSystem/ETS2020Audios/blob/master/Test${testNumber}/part3_${part3Index + 1}.mov?raw=true`;

  const imagePath = `https://raw.githubusercontent.com/QuizSystem/ETS2020Images/master/Test${testNumber}/part3_${part3Index + 1}.png`;
  const { width, height } = useWindowDimensions();
  const size = width > height ? height : width;

  const selectAnswer = (item: any, index: number, selected: string) => {
    onSelectAnswer(item, index, selected, part3Index);
  };

  const renderItem = ({ index }: { item: ItemType; index: number }) => {
    const item = data.questions[index];
    return (
      <QuestionItem
        item={item}
        index={index}
        onClick={selectAnswer}
      />
    );
  };

  return (
    <ItemContainer>
        <Text>{`${part3Index + 1}/ ${data.data.title || ''}`}</Text>
        {audioPath.length > 0 && <AudioPlayer audioPath={audioPath} play={part3Index == 0} />}
        {imagePath.length > 0 && part3Index >= 10 && <Image source={{ uri: imagePath }} size={size * 72 / 100} />}
        {data.submitted && !data.isCollapsed && (
          <HTMLContainer>
            <RawHTML>{data.data.html}</RawHTML>
          </HTMLContainer>
        )}
        {data.submitted && data.isShownTranslate && <Space />}
        {data.submitted && data.isShownTranslate && !data.isCollapsedTranslation && (
          <HTMLContainer>
            <RawHTML>{data.translate.value}</RawHTML>
          </HTMLContainer>
        )}
        {data.submitted && <Space />}
        <ListContainer>
          <TestFlatList
            data={data.questions}
            renderItem={renderItem}
            keyExtractor={(item, index) => `${index}_${item.question}`}
          />
        </ListContainer>
        {12 > part3Index && <Divider />}
    </ItemContainer>
  );
};

export default memo(Part3Item);
