import Screen from '../Screen';
import React, { FC, useState, useEffect } from 'react';
import { FlatList, Dimensions } from 'react-native';
import styled from 'styled-components/native';
import Fonts from '../../theme/fonts';
import Colors from '../../theme/colors';
import { useHistory, useParams } from 'react-router-dom';
import textAPI from "../../api/textAPI";
import CryptoJS from 'crypto-js';
import Part4Item from './shared/Part4Item';

const ButtonText = styled(Fonts.Bold)`
  font-size: 18px;
  background-color: ${Colors.BACKGROUND_COLOR};
  margin: 10px;
  padding: 10px;
`;

const TestFlatList = styled(FlatList as new () => FlatList<string>)`
  width: 100%;
  height: 100%;
`;

const Part4: FC = () => {
  const history = useHistory();
  const { testNumber } = useParams();

  const [testList, setTestList] = useState<any[]>([]);

  useEffect(() => {
    try {
      textAPI.get(testNumber, 4).then(data => {
        const plainData = CryptoJS.AES.decrypt(data, 'thieumao').toString(CryptoJS.enc.Utf8);
        const json = JSON.parse(plainData);
        console.log('>>>> Part 4');
        console.log(json);
        setTestList(json);
      });
    } catch (error) {}
  }, []);

  const renderItem = ({ index }: { item: any; index: number }) => {
    const item = testList[index];
    return (
      <Part4Item
        data={item}
        part4Index={index}
        visibleIndex={1}
        onSelectAnswer={() => null}
        onCollapse={() => null}
        onCollapseTranslation={() => null}
      />
    );
  };

  return (
      <Screen canBack title={`Test ${testNumber} - Part 4`} showMenu>
        <TestFlatList
          data={testList}
          renderItem={renderItem}
          keyExtractor={(item, index) => `${index}_${item}`}
        />
      </Screen>
  );
};

export default Part4;
