import Screen from '../Screen';
import React, { FC, useState, useEffect } from 'react';
import { FlatList } from 'react-native';
import styled from 'styled-components/native';
import { useParams } from 'react-router-dom';
import textAPI from 'api/textAPI';
import CryptoJS from 'crypto-js';
import Part6Item from './shared/Part6Item';
import Footer from 'components/Footer';
import Divider from '../../../components/Divider';

const TestFlatList = styled(FlatList as new () => FlatList<string>)`
  width: 100%;
  height: 100%;
`;

const Part6: FC = () => {
  const [testList, setTestList] = useState<any[]>([]);
  const [correctNumber, setCorrectNumber] = useState<number>(-1);
  const [totalNumber, setTotalNumber] = useState<number>(-1);

  const { testNumber } = useParams();

  useEffect(() => {
    try {
      textAPI.get(testNumber, 6).then(data => {
        const plainData = CryptoJS.AES.decrypt(data, 'thieumao').toString(CryptoJS.enc.Utf8);
        const json = JSON.parse(plainData);
        console.log('>>>> Part 6');
        console.log(json);
        setTestList(json);
      });
    } catch (error) {}
  }, []);

  const onReset = () => {};

  const onSubmit = () => {};

  const renderItem = ({ index }: { item: any; index: number }) => {
    const item = testList[index];
    return (
      <Part6Item
        data={item}
        part6Index={index}
        visibleIndex={1}
        onSelectAnswer={() => null}
        onCollapse={() => null}
        onCollapseTranslation={() => null}
      />
    );
  };

  return (
      <Screen canBack title={`Test ${testNumber} - Part 6`} showMenu>
        <TestFlatList
          data={testList}
          renderItem={renderItem}
          keyExtractor={(item, index) => `${index}_${item}`}
        />
        <Footer correctNumber={correctNumber} totalNumber={totalNumber} onReset={() => onReset()} onSubmit={() => onSubmit()} />
      </Screen>
  );
};

export default Part6;
