import React, { memo } from 'react';

const RawHTML = ({ children }) => {
  let html = children.replace(/\n/g, '<br />');
  html = html.replace('<p>&nbsp;</p>', '');
  return(
    <div
      dangerouslySetInnerHTML={{ __html: html}} 
    />
  );
}

export default memo(RawHTML);
