import React, { FC, memo } from 'react';
import { Dimensions, FlatList } from 'react-native';
import styled from 'styled-components/native';
import Fonts from '../../../theme/fonts';
import Colors from '../../../theme/colors';
import QuestionItem from './QuestionItem';
import RawHTML from '../../../components/RawHTML';

interface Props {
  data: any;
  part3Index: number;
  visibleIndex: number;
  onSelectAnswer: (detailItem: any, questionPosition: number, selected: string, part3Index: number) => void;
  onCollapse: (part3Index: number) => void;
  onCollapseTranslation: (part3Index: number) => void;
}

interface StyledProps {
  height?: number;
  width?: number;
  marginHorizontal?: number;
  paddingHorizontal?: number;
  paddingRight?: number;
  isCorrect?: boolean;
  isSelected?: boolean;
  isShown?: boolean;
}

interface ItemType {
  id: number;
  a: string;
  b: string;
  c: string;
  d: string;
  correct: string;
  question: string;
  selected?: string;
  submitted?: boolean;
}

const ScrollView = styled.ScrollView`
  padding-horizontal: 4px;
`;

const ItemContainer = styled.View<StyledProps>`
  width: ${props => props.width}px;
  background-color: ${Colors.SECOND_COLOR};
`;

const Text = styled(Fonts.Bold)`
  font-size: 18px;
  padding: 8px;
`;

const TestFlatList = styled(FlatList as new () => FlatList<ItemType>)`
  width: 100%;
  height: 100%;
`;

const ListContainer = styled.View`
  height: 100%;
  width: 100%;  
  padding-vertical: 8px;
`;

const Space = styled.View<StyledProps>`
  background-color: ${Colors.MAIN_COLOR};
  width: 100%;
  height: 1px;
  margin-top: 12px;
`;

const Button = styled.TouchableOpacity`
  justify-content: center;
  align-items: flex-end;
  padding-horizontal: 4px;
`;

const EmptyView = styled.View`
  width: 100%;
  height: 100%;
`;

const HTMLContainer = styled.View`
  padding: 12px;
`;

const Part3Item: FC<Props> = ({
  data,
  part3Index,
  visibleIndex,
  onSelectAnswer,
  onCollapse,
  onCollapseTranslation,
}) => {
  const selectAnswer = (item: any, index: number, selected: string) => {
    onSelectAnswer(item, index, selected, part3Index);
  };

  const renderItem = ({ index }: { item: ItemType; index: number }) => {
    const item = data.questions[index];
    return (
      <QuestionItem
        item={item}
        index={index}
        onClick={selectAnswer}
      />
    );
  };

  console.log('data.html = ');
  console.log(data);
  console.log(data.data.html);
  console.log(data.html);
  return (
    <ItemContainer>
        <Text>{`${part3Index + 1}/ ${data.data.title || ''}`}</Text>
        <Space />
        {!data.isCollapsed && (
          <HTMLContainer>
            <RawHTML>{data.data.html}</RawHTML>
          </HTMLContainer>
        )}
        {data.isShownTranslate && <Space />}
        {data.isShownTranslate && !data.isCollapsedTranslation && <RawHTML>{data.translate.value}</RawHTML>}
        <Space />
        <ListContainer>
          <TestFlatList
            data={data.questions}
            renderItem={renderItem}
            keyExtractor={(item, index) => `${index}_${item.question}`}
          />
        </ListContainer>
    </ItemContainer>
  );
};

export default memo(Part3Item);
