import React, { FC, memo } from 'react';
import { TouchableOpacity, Dimensions } from 'react-native';
import styled from 'styled-components/native';
import Fonts from '../../../theme/fonts';
import Colors from '../../../theme/colors';
import { Answer } from '../../../constants/AppConstants';
import Divider from '../../../components/Divider';
import RawHTML from '../../../components/RawHTML';

interface Props {
  item: any;
  index: number;
  visibleIndex: number;
  onClickA: (item: any, index: number, selected: string) => void;
  onClickB: (item: any, index: number, selected: string) => void;
  onClickC: (item: any, index: number, selected: string) => void;
  onClickD: (item: any, index: number, selected: string) => void;
}

interface StyledProps {
  width?: number;
  height?: number;
  marginHorizontal?: number;
  paddingHorizontal?: number;
  paddingRight?: number;
  isCorrect?: boolean;
  isSelected?: boolean;
  isShown?: boolean;
}

const Text = styled(Fonts.Bold)`
  font-size: 18px;
  padding: 8px;
`;

const ScrollView = styled.ScrollView`
  padding-horizontal: 4px;
`;

const ItemContainer = styled.View<StyledProps>`
  width: 100%;
  background-color: ${Colors.SECOND_COLOR};
`;

const ButtonText = styled(Fonts.Normal) <StyledProps>`
  font-size: 18px;
  color: ${props => props.isSelected ? 'green' : 'black'};
  background-color: ${props => props.isShown ? 'yellow' : Colors.SECOND_COLOR};
  padding: 8px;
`;

const EmptyView = styled.View`
  width: 100%;
  height: 100%;
`;

const HTMLContainer = styled.View`
  margin: 12px;
`;

const Part5Item: FC<Props> = ({ item, index, visibleIndex, onClickA, onClickB, onClickC, onClickD }) => {
  // render Empty View
  // if (index < visibleIndex - 1 || index > visibleIndex + 1) {
  //   return (
  //     <ItemContainer width={itemWidth} height={Dimensions.get('window').height}>
  //       <EmptyView />
  //     </ItemContainer>
  //   );
  // }
  return (
    <ItemContainer>
      <ScrollView>
        <Text >{`${index + 1}/ ${item.question}`}</Text>
        <Divider />
        <TouchableOpacity onPress={() => onClick(item, index, Answer.A)}>
          <ButtonText isSelected={Answer.A == item.selected} isShown={item.submitted && Answer.A == item.correct}>{`A. ${item.a}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        <TouchableOpacity onPress={() => onClick(item, index, Answer.B)}>
          <ButtonText isSelected={Answer.B == item.selected} isShown={item.submitted && Answer.B == item.correct}>{`B. ${item.b}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        <TouchableOpacity onPress={() => onClick(item, index, Answer.C)}>
          <ButtonText isSelected={Answer.C == item.selected} isShown={item.submitted && Answer.C == item.correct}>{`C. ${item.c}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        <TouchableOpacity onPress={() => onClick(item, index, Answer.D)}>
          <ButtonText isSelected={Answer.D == item.selected} isShown={item.submitted && Answer.D == item.correct}>{`D. ${item.d}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        {item.submitted && item.html && (
          <HTMLContainer>
            <RawHTML>{item.html}</RawHTML>
          </HTMLContainer>
        )}
      </ScrollView>
    </ItemContainer>
  );
};

export default memo(Part5Item);
