import React, { useRef } from 'react';

const AudioPlayer = ({ audioPath, play }) => {
  const audioRef = useRef();

  const handleLoadedData = () => {

    audioRef.current.play();
  };

  return (
    <audio
      ref={audioRef}
      src={audioPath}
      onLoadedData={handleLoadedData}
    />
  );
};

export default AudioPlayer;
