import React, { FC, memo } from 'react';
import { TouchableOpacity } from 'react-native';
import styled from 'styled-components/native';
import Fonts from '../../../theme/fonts';
import Colors from '../../../theme/colors';
import { Answer } from '../../../constants/AppConstants';
import Divider from '../../../components/Divider';
import RawHTML from '../../../components/RawHTML';
import AudioPlayer from 'components/AudioPlayer';

interface Props {
  item: any;
  index: number;
  testNumber: number;
  onClick: (item: any, index: number, selected: string) => void;
}

interface StyledProps {
  width?: number;
  height?: number;
  marginHorizontal?: number;
  paddingHorizontal?: number;
  paddingRight?: number;
  isCorrect?: boolean;
  isSelected?: boolean;
  isShown?: boolean;
}

const Text = styled(Fonts.Bold)`
  font-size: 18px;
  padding: 8px;
`;

const ScrollView = styled.ScrollView`
  padding-horizontal: 4px;
`;

const ItemContainer = styled.View<StyledProps>`
  width: 100%;
  background-color: ${Colors.SECOND_COLOR};
`;

const ButtonText = styled(Fonts.Normal) <StyledProps>`
  font-size: 18px;
  color: ${props => props.isSelected ? 'green' : 'black'};
  background-color: ${props => props.isShown ? 'yellow' : Colors.SECOND_COLOR};
  padding: 8px;
`;

const HTMLContainer = styled.View`
  margin: 12px;
`;

const Part2Item: FC<Props> = ({ item, index, testNumber, onClick }) => {
  const audioPath = `https://github.com/QuizSystem/ETS2020Audios/blob/master/Test${testNumber}/part2_${index + 7}.mov?raw=true`;

  return (
    <ItemContainer>
      <ScrollView>
        <Text >{`${index + 1}/ ${item.question || ''}`}</Text>
        {audioPath.length > 0 && <AudioPlayer audioPath={audioPath} play={index == 0} />}
        <Divider />
        <TouchableOpacity onPress={() => onClick(item, index, Answer.A)}>
          <ButtonText isSelected={Answer.A == item.selected} isShown={item.submitted && Answer.A == item.correct}>{`A. ${item.submitted ? item.a : ''}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        <TouchableOpacity onPress={() => onClick(item, index, Answer.B)}>
          <ButtonText isSelected={Answer.B == item.selected} isShown={item.submitted && Answer.B == item.correct}>{`B. ${item.submitted ? item.b : ''}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        <TouchableOpacity onPress={() => onClick(item, index, Answer.C)}>
          <ButtonText isSelected={Answer.C == item.selected} isShown={item.submitted && Answer.C == item.correct}>{`C. ${item.submitted ? item.c : ''}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        {item.submitted && item.html && (
          <HTMLContainer>
            <RawHTML>{item.html}</RawHTML>
          </HTMLContainer>
        )}
      </ScrollView>
    </ItemContainer>
  );
};

export default memo(Part2Item);
