import React, { FC, memo, useRef } from 'react';
import { TouchableOpacity } from 'react-native';
import styled from 'styled-components/native';
import Fonts from '../../../theme/fonts';
import Colors from '../../../theme/colors';
import { Answer } from '../../../constants/AppConstants';
import Divider from '../../../components/Divider';
import RawHTML from '../../../components/RawHTML';
import useWindowDimensions from 'hooks/useWindowDimensions';

interface Props {
  item: any;
  index: number;
  testNumber: number;
  onClickA: (item: any, index: number, selected: string) => void;
  onClickB: (item: any, index: number, selected: string) => void;
  onClickC: (item: any, index: number, selected: string) => void;
  onClickD: (item: any, index: number, selected: string) => void;
}

interface StyledProps {
  size?: number;
  width?: number;
  height?: number;
  marginHorizontal?: number;
  paddingHorizontal?: number;
  paddingRight?: number;
  isCorrect?: boolean;
  isSelected?: boolean;
  isShown?: boolean;
}

const Text = styled(Fonts.Bold)`
  font-size: 18px;
  padding: 8px;
`;

const ScrollView = styled.ScrollView`
  padding-horizontal: 4px;
`;

const ItemContainer = styled.View<StyledProps>`
  width: 100%;
  background-color: ${Colors.SECOND_COLOR};
`;

const ButtonText = styled(Fonts.Normal) <StyledProps>`
  font-size: 18px;
  color: ${props => props.isSelected ? 'green' : 'black'};
  background-color: ${props => props.isShown ? 'yellow' : Colors.SECOND_COLOR};
  padding: 8px;
`;

const EmptyView = styled.View`
  width: 100%;
  height: 100%;
`;

const HTMLContainer = styled.View`
  margin: 12px;
`;

// width: ${props => props.width}px;
// height: ${props => props.height}px;
// width: ${props => props.size}%;
//   height: ${props => props.size}%;
const Image = styled.Image<StyledProps>`
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  resize-mode: contain;
  align-self: center;
`;

const Part1Item: FC<Props> = ({ item, index, testNumber, onClickA, onClickB, onClickC, onClickD }) => {
  const audioRef = useRef();
  const audioPath = `https://github.com/QuizSystem/ETS2020Audios/blob/master/Test${testNumber}/part1_${index + 1}.mov?raw=true`;
  const handleLoadedData = () => {
    setDuration(audioRef.current.duration);
    if (isPlay) audioRef.current.play();
  };

  const imagePath = `https://raw.githubusercontent.com/QuizSystem/ETS2020Images/master/Test${testNumber}/part1_${index + 1}.png`;
  const { width, height } = useWindowDimensions();
  const size = width > height ? height : width;
  return (
    <ItemContainer>
      <ScrollView>
        <Text >{`${index + 1}/`}</Text>
        {imagePath.length > 0 && <Image source={{ uri: imagePath }} size={size * 72 / 100} />}
        <audio
          src={audioPath}
          onLoadedData={handleLoadedData}
        />
        <Divider />
        <TouchableOpacity onPress={() => onClickA(item, index, Answer.A)}>
          <ButtonText isSelected={Answer.A == item.selected} isShown={item.submitted && Answer.A == item.correct}>{`A. ${item.a}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        <TouchableOpacity onPress={() => onClickB(item, index, Answer.B)}>
          <ButtonText isSelected={Answer.B == item.selected} isShown={item.submitted && Answer.B == item.correct}>{`B. ${item.b}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        <TouchableOpacity onPress={() => onClickC(item, index, Answer.C)}>
          <ButtonText isSelected={Answer.C == item.selected} isShown={item.submitted && Answer.C == item.correct}>{`C. ${item.c}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        <TouchableOpacity onPress={() => onClickD(item, index, Answer.D)}>
          <ButtonText isSelected={Answer.D == item.selected} isShown={item.submitted && Answer.D == item.correct}>{`D. ${item.d}`}</ButtonText>
        </TouchableOpacity>
        <Divider />
        {item.submitted && item.html && (
          <HTMLContainer>
            <RawHTML>{item.html}</RawHTML>
          </HTMLContainer>
        )}
      </ScrollView>
    </ItemContainer>
  );
};

export default memo(Part1Item);
