import Screen from '../Screen';
import React, { FC, useState, useEffect } from 'react';
import { FlatList, Dimensions } from 'react-native';
import styled from 'styled-components/native';
import Fonts from 'theme/fonts';
import Colors from 'theme/colors';
import { useHistory, useParams } from 'react-router-dom';
import textAPI from "api/textAPI";
import CryptoJS from 'crypto-js';
import Part5Item from './shared/Part5Item';
import Footer from 'components/Footer';
import { getNumber } from 'store/selectors/projectSelectors';
import { testAction, increaseAction } from 'store/actionTypes/actionType';
import { useDispatch, useSelector } from 'react-redux';

const ButtonText = styled(Fonts.Bold)`
  font-size: 18px;
  background-color: ${Colors.BACKGROUND_COLOR};
  margin: 10px;
  padding: 10px;
`;

const TestFlatList = styled(FlatList as new () => FlatList<string>)`
  width: 100%;
  height: 100%;
`;

const Part5: FC = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const { testNumber } = useParams();

  const [testList, setTestList] = useState<any[]>([]);

  const valueNumber = useSelector(getNumber);

  useEffect(() => {
    console.log("Introduction");
    dispatch(testAction('name', 'Thieu Mao'));
    dispatch(increaseAction('number', valueNumber));
  }, []);

  useEffect(() => {
    try {
      textAPI.get(testNumber, 5).then(data => {
        const plainData = CryptoJS.AES.decrypt(data, 'thieumao').toString(CryptoJS.enc.Utf8);
        const json = JSON.parse(plainData);
        setTestList(json);
      });
    } catch (error) {}
  }, []);

  const selectAnswer = (item: any, index: number, selected: string) => {
    console.log('>>>>>> selectAnswer');
    console.log(item);
    console.log(index);
    console.log(selected);
  };

  const renderItem = ({ index }: { item; index }) => {
    const item = testList[index];
    return (
      <Part5Item
        item={item}
        index={index}
        visibleIndex={1}
        onClick={selectAnswer}
      />
    );
  };

  return (
      <Screen canBack title={`Test ${testNumber} - Part 5`} showMenu>
        <TestFlatList
          data={testList}
          renderItem={renderItem}
          keyExtractor={(item, index) => `${index}_${item}`}
        />
        <Footer correctNumber={1} totalNumber={20} onReset={() => null} onSubmit={() => null} />
      </Screen>
  );
};

export default Part5;
